mapper.select:
SELECT A.seq, A.photo_uciCode, A.ipAddress, A.regDate, B.copyright FROM `download` A INNER JOIN photo B ON A.photo_uciCode = B.uciCode WHERE A.member_seq = #{member_seq}
AND YEAR(A.regDate) = #{year}
ORDER BY A.regDate DESC

SELECT A.compName, A.id, A.name, B.photo_uciCode as uciCode, B.regDate, (SELECT compName FROM member INNER JOIN photo ON member.seq = photo.member_seq WHERE photo.uciCode = B.photo_uciCode) AS media FROM member A INNER JOIN download B ON A.seq = B.member_seq WHERE ( A.name LIKE CONCAT('%', #{keyword}, '%') OR A.compName LIKE CONCAT('%', #{keyword}, '%') OR A.id LIKE CONCAT('%', #{keyword}, '%') OR B.photo_uciCode LIKE CONCAT('%', #{keyword}, '%') OR (SELECT compName FROM member INNER JOIN photo ON member.seq = photo.member_seq WHERE photo.uciCode = B.photo_uciCode) LIKE CONCAT('%', #{keyword}, '%') )
AND B.regDate BETWEEN #{contractStart} AND #{contractEnd}
ORDER BY B.regDate DESC LIMIT #{startPage}, #{pageVol}

SELECT count(A.seq) as cnt FROM member A INNER JOIN download B ON A.seq = B.member_seq WHERE ( A.name LIKE CONCAT('%', #{keyword}, '%') OR A.compName LIKE CONCAT('%', #{keyword}, '%') OR A.id LIKE CONCAT('%', #{keyword}, '%') OR B.photo_uciCode LIKE CONCAT('%', #{keyword}, '%') OR (SELECT compName FROM member INNER JOIN photo ON member.seq = photo.member_seq WHERE photo.uciCode = B.photo_uciCode) LIKE CONCAT('%', #{keyword}, '%') )
AND B.regDate BETWEEN #{contractStart} AND #{contractEnd}
ORDER BY B.regDate DESC

SELECT m.compName, m.name, m.id, m.seq, m.phone FROM download down INNER JOIN member m ON down.member_seq = m.seq WHERE m.deferred = 2 AND m.seq NOT IN (SELECT member_seq FROM paymentManage)
AND (m.compName LIKE #{keyword} OR m.name LIKE #{keyword} OR m.id LIKE #{keyword})
ORDER BY down.regDate DESC LIMIT #{startPage}, #{pageVol}
