mapper.select:
SELECT * FROM member
WHERE name = #{name}
AND phone= #{phone}

	    SELECT A.*, B.groupName FROM member A 
	    LEFT JOIN `group` B ON A.group_seq = B.seq
	    
	        A.withdraw = 0 
             
	            AND ( `id` LIKE CONCAT('%',#{keyword},'%') OR `name` LIKE CONCAT('%',#{keyword},'%') OR `compName` LIKE CONCAT('%',#{keyword},'%') )
            
            	        
	         
	            AND `type` = #{type}
            
            
			 
				AND `deferred` = #{deferred}    
			
			
			 
				AND `group_seq` is NULL    
			
			
			 
				AND `group_seq` is not NULL    
			
			
		
		ORDER BY A.regDate DESC 
		LIMIT #{startPage}, #{pageVol}

SELECT (SELECT id FROM member WHERE seq = adjustment.adjMaster) AS masterID, member.seq, id, email, name, phone, compNum, compName, preRate, postRate, activate, logo FROM member LEFT JOIN adjustment ON member.seq = adjustment.adjSlave WHERE member.type='M' AND member.admission = 'Y' AND member.withdraw = 0
AND ( `id` LIKE CONCAT('%',#{keyword},'%') OR `name` LIKE CONCAT('%',#{keyword},'%') OR `compName` LIKE CONCAT('%',#{keyword},'%') )
ORDER BY member.regDate DESC LIMIT #{startPage}, #{pageVol}
