mapper.select:
		SELECT * FROM member
		where id = #{id}
		
			AND pw = #{pw}

mapper.insert:
		INSERT INTO member (
		id, pw, email ,name, phone, type
		, compNum
		, compDocPath
		, compName
		, compAddress
		, compAddDetail
		, compZipcode
		, compTel
		, logo
		) values (
		#{id},#{pw},#{email},#{name},#{phone},#{type}
		, #{compNum}
		, #{compDocPath}
		, #{compName}
		, #{compAddress}
		, #{compAddDetail}
		, #{compZipcode}
		, #{compTel}
		, #{logo}
		)

mapper.update:
update member

	name = #{name}
	, email = #{email}
	, pw = #{pw}
	, phone = #{phone}
	, type = #{type}
	, compNum = #{compNum}
	, compDocPath = #{compDocPath}
	, compName = #{compName}
	, compAddress = #{compAddress}
	, compAddDetail = #{compAddDetail}
	, compZipcode = #{compZipcode}
	, compTel = #{compTel}
	, logo = #{logo}

where seq = #{seq}
