mapper.update:
UPDATE photo 
	SET
		widthPixel = #{widthPx}
		, heightPixel = #{heightPx}
		, widthCm = #{widthCm}
		, heightCm = #{heightCm}
		, dpi = #{dpi}
		, fileSize = #{fileSize}
		, exif = #{exif}
		, uciStat = '-1'
	WHERE uciCode = #{uciCode}

mapper.select:
    SELECT B.bookmark_seq, M.compName AS copyright, A.uciCode, A.saleStat as saleState, M.admission, M.withdraw, M.activate FROM photo A
	INNER JOIN bookmarkPhoto B ON A.uciCode = B.photo_uciCode 
	INNER JOIN bookmark C ON B.bookmark_seq = C.seq
	INNER JOIN photo P ON B.photo_uciCode = P.uciCode
	INNER JOIN member M ON P.member_seq = M.seq
	WHERE C.member_seq = #{member_seq}
	
		AND B.bookmark_seq = #{bookmark_seq}
			
    ORDER BY B.regDate DESC
	
		LIMIT #{start}, #{pageVol}

mapper.update:
UPDATE 
	photo SET hitCount = hitCount + 1 
WHERE uciCode = #{uciCode}

mapper.select:
SELECT SUM(PD.downCount) as downCount, PD.photo_uciCode, P.widthCm, P.heightCm, P.uciCode, M.name AS ownerName FROM paymentDetail PD INNER JOIN photo P ON PD.photo_uciCode = P.uciCode INNER JOIN member M ON P.member_seq = M.seq WHERE uciCode NOT IN (SELECT photo_uciCode FROM photoBlock) AND P.saleStat = 1 AND member_seq IN
#{seq}
GROUP BY photo_uciCode ORDER BY downCount DESC, uciCode ASC LIMIT #{start}, #{count}

SELECT A.widthCm, A.heightCm, A.uciCode, M.compName AS ownerName, COUNT(*) AS zzimCount FROM photo A INNER JOIN bookmarkPhoto B ON A.uciCode = B.photo_uciCode INNER JOIN member M ON A.member_seq = M.seq WHERE A.saleStat = 1 AND M.activate = 1 AND A.uciCode NOT IN (SELECT photo_uciCode FROM photoBlock) AND member_seq IN
#{seq}
GROUP BY B.photo_uciCode HAVING zzimCount > 0 ORDER BY zzimCount DESC, uciCode ASC LIMIT #{start}, #{count}

SELECT P.widthCm, P.heightCm, P.uciCode, P.hitCount, M.compName AS ownerName FROM photo P LEFT JOIN member M ON P.member_seq = M.seq WHERE P.saleStat = 1 AND P.uciCode NOT IN (SELECT photo_uciCode FROM photoBlock) AND member_seq IN
#{seq}
ORDER BY hitCount DESC LIMIT #{start}, #{count}

SELECT seq, exhName, exhDes, exhKeyword, exhUpper, exhType, regDate 
FROM exhibitionList 
WHERE exhName = #{exhName}
