mapper.select:
SELECT seq AS usageList_seq, `usage`, division1, division2, division3, division4, usageDate, price FROM usageList where `active` = 'Y'
AND individual = #{individual}
ORDER BY seq, `usage`, division1, division2, division3, division4, usageDate, price

    SELECT A.seq AS usageList_seq, A.usage, A.division1, A.division2, A.division3, A.division4, A.usageDate, A.price FROM usageList A
	INNER JOIN basket B ON A.seq = B.usageList_seq
	WHERE B.member_seq = #{member_seq} AND B.photo_uciCode = #{uciCode}

SELECT seq 
FROM usageList
WHERE `usage` = #{usage} AND `price` = #{price} AND `individual` = #{paramSeq} AND `active` = 'N' ORDER BY seq DESC LIMIT 1;
